import './App.css';
import { getAuth, createUserWithEmailAndPassword, signInWithEmailAndPassword } from "firebase/auth";
import app from './firebase';
import { useState } from 'react';
import firebase from 'firebase/compat/app';
import 'firebase/compat/auth';
import 'firebase/compat/firestore';
import { getDatabase, child, ref, onValue, get } from "firebase/database";

function App() {

  var profile1 = [];

  const auth = getAuth(app);

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const signUp = () => {

    createUserWithEmailAndPassword(auth, email, password)
      .then((userCredential) => {
        // Signed in 
        const user = userCredential.user;
        console.log("created account")
        // ...
      })
      .catch((error) => {
        const errorCode = error.code;
        console.log("error")
        //const errorMessage = error.message;
        // ..
      });
  }

  const logIn = () => {

    signInWithEmailAndPassword(auth, email, password)
      .then((userCredential) => {
        // Signed in 
        const user = userCredential.user;
        console.log("logued account")
        getInfoDb();
      })
      .catch((error) => {
        const errorCode = error.code;
        console.log(errorCode);
      });
  }

  const getInfoDb = () => {
    const db = getDatabase();
    const dbRef = ref(db);
    get(child(dbRef, 'profile'))
      .then((snapshot) => {
        var timbres = [];
        snapshot.forEach(childSnapshot => {
          timbres.push(childSnapshot.val());
        });
        console.log(timbres);
        profile1 = timbres;
      })
      .then(() => {
        console.log(profile1[4]);
      })
      .then(() => { //---- REGISTRO DE FECHA/HORARIO DEL TIMBRE PRESIONADO ----
        if (profile1[4].doorBell == true) {
          const current = new Date();
          const date = `${current.getDate()}/${current.getMonth() + 1}/${current.getFullYear()}`;
          const time = current.toLocaleTimeString("en-US", {
            hour: "2-digit",
            minute: "2-digit",
            second: "2-digit"
          });
          console.log("el timbre se presionó")
          console.log(`a las ${time} el día ${date}`)

        }
      })
      .then(() => {//---- SI LA PUERTA CAMBIA A TRUE MANDAR NOTIFIACIÓN ----
        if (profile1[4].unlockDoor == false) { console.log("MANDAR NOTIFIACIÓN DE Q SE ABRIÓ LA PUERTA") }
      })

  }

  return (
    <div className="main">
      <div className="App">
        <input type={"email"} placeholder="email" onChange={(e) => setEmail(e.target.value)} />
        <input type={"password"} placeholder="pass" onChange={(e) => setPassword(e.target.value)} />
        <button onClick={signUp}>Crear cuenta</button>
        <button onClick={logIn}>Ingresar</button>
      </div>
    </div>
  );

}

export default App;
