import './App.css';
import { getAuth, createUserWithEmailAndPassword, signInWithEmailAndPassword } from "firebase/auth";
import app from './firebase';
import { useState } from 'react';
import firebase from 'firebase/compat/app';
import 'firebase/compat/auth';
import 'firebase/compat/firestore';
import { getDatabase, child, ref, onValue, get } from "firebase/database";
import 'firebase/compat/database';

function App() {

  var profile1 = [];
  var current;
  var date;
  var time;
  var cardsLength;
  var flagsLength;
  var startTime;
  var endTime;
  var userName;
  var idUser;
  var cardScan;
  var doorBell;
  var sendNotification;
  var unlockDoor;
  var enabled;

  const db = getDatabase();
  const dbRef = ref(db);

  const auth = getAuth(app);

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const signUp = () => {

    createUserWithEmailAndPassword(auth, email, password)
      .then((userCredential) => {
        // Signed in 
        const user = userCredential.user;
        console.log("created account")

      })
      .catch((error) => {
        const errorCode = error.code;
        console.log("error")
      });
  }

  const logIn = () => {

    signInWithEmailAndPassword(auth, email, password)
      .then((userCredential) => {
        // Signed in 
        const user = userCredential.user;
        console.log("logued account")
        getInfoDb();
        /* el login debe iniciar con display block y el div de la app con display none. cuando se ejecuta la funcion
        del login deben inventirse las etiquetas.
         document.querySelector("#app").style.display = "block";
         document.querySelector(".login").style.display = "none";*/
      })
      .catch((error) => {
        const errorCode = error.code;
        console.log(errorCode);
      });
  }

  const getInfoDb = () => {
    get(child(dbRef, 'profile'))
      .then((snapshot) => {
        var timbres = [];
        snapshot.forEach(childSnapshot => {
          timbres.push(childSnapshot.val());
        });
        console.log(timbres);
        profile1 = timbres;
      })
      .then(() => {
